/**
 * Уведомления в сплывающих окнах от процессов
 * string @param type  Тип события (good, error)
 * string @param message   Сообщение на вывод
 */
function notify_show(type, message) {
    if(type === 'error'){
        new Noty({
            type: type,
            theme: 'mint',
            layout: 'center',
            modal: true,
            closeWith: ['click', 'button'],
            text: '<i class="uk-icon-spin uk-icon-refresh"></i> '+ message
        }).show();
    }else{
        if(type === 'message'){
            type = 'info';
        }
        new Noty({
            type: type,
            theme: 'mint',
            layout: 'topRight',
            timeout: 3000,
            closeWith: ['click', 'button'],
            text: '<i class="uk-icon-check"></i> '+ message
        }).show();
    }
}

